import React, { useRef, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const Login = () => {
  const user = useRef();
  const pass = useRef();
  const [error, setError] = useState(false);

  const ingresar = () => {
    const usuario = user.current.value;
    const password = pass.current.value;

    // Simulación de autenticación
    if (usuario === 'admin' && password === '1234') {
      setError(false);
      alert('Login exitoso');
    } else {
      setError(true);
    }
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100 bg-light">
      <div className="card shadow-lg p-4" style={{ width: '100%', maxWidth: '400px', borderRadius: '20px' }}>
        <h3 className="text-center mb-4" style={{ color: '#dc5c8c' }}>Iniciar Sesión</h3>

        <div className="mb-3">
          <label htmlFor="txtUser" className="form-label">Usuario:</label>
          <input type="text" id="txtUser" ref={user} className="form-control" placeholder="Ingrese su usuario" />
        </div>

        <div className="mb-3">
          <label htmlFor="txtPass" className="form-label">Contraseña:</label>
          <input type="password" id="txtPass" ref={pass} className="form-control" placeholder="Ingrese su contraseña" />
        </div>

        <div className="d-grid">
          <button className="btn btn-primary" style={{ backgroundColor: '#dc5c8c', border: 'none' }} onClick={ingresar}>
            Ingresar
          </button>
        </div>

        {error && <p className="text-danger mt-3 text-center">Usuario y/o contraseña incorrectos</p>}
      </div>
    </div>
  );
};

export default Login;
